const deg=6,hr=document.querySelector("#hr"),mn=document.querySelector("#mn"),sc=document.querySelector("#sc");setInterval(()=>{let e=new Date,t=30*e.getHours(),r=e.getMinutes()*deg,o=e.getSeconds()*deg;hr.style.transform=`rotateZ(${t+r/12}deg)`,mn.style.transform=`rotateZ(${r}deg)`,sc.style.transform=`rotateZ(${o}deg)`});